import * as types from "../../actionTypes";
import Academic_yearApi from "../../../api/Academic_yearApi";

let actionsFunction = {

  //CRUD METHODS

  // Create academic_year
  createAcademic_year: function(academic_year) {
    return function(dispatch) {
      return Academic_yearApi
        .createAcademic_year(academic_year)
        .then(academic_year => {
          dispatch(actionsFunction.createAcademic_yearSuccess(academic_year));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createAcademic_yearSuccess: function(academic_year) {
    return { type: types.CREATE_ACADEMIC_YEAR_SUCCESS, payload: academic_year };
  },


  // Delete academic_year
  deleteAcademic_year: function(id) {
    return function(dispatch) {
      return Academic_yearApi
        .deleteAcademic_year(id)
        .then(academic_year => {
          dispatch(actionsFunction.deleteAcademic_yearSuccess(academic_year));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteAcademic_yearSuccess: function(academic_year) {
    return { type: types.DELETE_ACADEMIC_YEAR_SUCCESS, payload: academic_year };
  },


  // Get academic_year
  loadAcademic_year: function(id) {
    return function(dispatch) {
      return Academic_yearApi
        .getOneAcademic_year(id)
        .then(academic_year => {
          dispatch(actionsFunction.loadAcademic_yearSuccess(academic_year));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadAcademic_yearSuccess: function(academic_year) {
    return { type: types.GET_ACADEMIC_YEAR_SUCCESS, payload: academic_year };
  },

  // Load  list
  loadAcademic_yearList: function() {
    return function(dispatch) {
      return Academic_yearApi
        .getAcademic_yearList()
        .then(list => {
          dispatch(actionsFunction.loadAcademic_yearListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadAcademic_yearListSuccess: function(list) {
    return { type: types.LIST_ACADEMIC_YEAR_SUCCESS, payload: list };
  },

	
  // Save academic_year
  saveAcademic_year: function(academic_year) {
    return function(dispatch) {
      return Academic_yearApi
        .saveAcademic_year(academic_year)
        .then(academic_year => {
          dispatch(actionsFunction.saveAcademic_yearSuccess(academic_year));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveAcademic_yearSuccess: function(academic_year) {
    return { type: types.UPDATE_ACADEMIC_YEAR_SUCCESS, payload: academic_year };
  },


};

export default actionsFunction;
