import * as types from "../../actionTypes";
import SchoolApi from "../../../api/SchoolApi";

let actionsFunction = {

  //CRUD METHODS

  // Create school
  createSchool: function(school) {
    return function(dispatch) {
      return SchoolApi
        .createSchool(school)
        .then(school => {
          dispatch(actionsFunction.createSchoolSuccess(school));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createSchoolSuccess: function(school) {
    return { type: types.CREATE_SCHOOL_SUCCESS, payload: school };
  },


  // Delete school
  deleteSchool: function(id) {
    return function(dispatch) {
      return SchoolApi
        .deleteSchool(id)
        .then(school => {
          dispatch(actionsFunction.deleteSchoolSuccess(school));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteSchoolSuccess: function(school) {
    return { type: types.DELETE_SCHOOL_SUCCESS, payload: school };
  },


  // Get school
  loadSchool: function(id) {
    return function(dispatch) {
      return SchoolApi
        .getOneSchool(id)
        .then(school => {
          dispatch(actionsFunction.loadSchoolSuccess(school));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadSchoolSuccess: function(school) {
    return { type: types.GET_SCHOOL_SUCCESS, payload: school };
  },

  // Load  list
  loadSchoolList: function() {
    return function(dispatch) {
      return SchoolApi
        .getSchoolList()
        .then(list => {
          dispatch(actionsFunction.loadSchoolListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadSchoolListSuccess: function(list) {
    return { type: types.LIST_SCHOOL_SUCCESS, payload: list };
  },

	
  // Save school
  saveSchool: function(school) {
    return function(dispatch) {
      return SchoolApi
        .saveSchool(school)
        .then(school => {
          dispatch(actionsFunction.saveSchoolSuccess(school));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveSchoolSuccess: function(school) {
    return { type: types.UPDATE_SCHOOL_SUCCESS, payload: school };
  },


};

export default actionsFunction;
