import * as types from "../../actionTypes";
import StudentApi from "../../../api/StudentApi";

let actionsFunction = {

  //CRUD METHODS

  // Create student
  createStudent: function(student) {
    return function(dispatch) {
      return StudentApi
        .createStudent(student)
        .then(student => {
          dispatch(actionsFunction.createStudentSuccess(student));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createStudentSuccess: function(student) {
    return { type: types.CREATE_STUDENT_SUCCESS, payload: student };
  },


  // Delete student
  deleteStudent: function(id) {
    return function(dispatch) {
      return StudentApi
        .deleteStudent(id)
        .then(student => {
          dispatch(actionsFunction.deleteStudentSuccess(student));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteStudentSuccess: function(student) {
    return { type: types.DELETE_STUDENT_SUCCESS, payload: student };
  },


  // Find by academic_id
  findByacademic_id: function(key) {
    return function(dispatch) {
      return StudentApi
        .findByacademic_id(key)
        .then(item => {
          dispatch(actionsFunction.findByacademic_idSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  findByacademic_idSuccess: function(item) {
    return { type: types.FINDBYACADEMIC_ID_STUDENT_SUCCESS, payload: item };
  },


  // Find by classs_id
  findByclasss_id: function(key) {
    return function(dispatch) {
      return StudentApi
        .findByclasss_id(key)
        .then(item => {
          dispatch(actionsFunction.findByclasss_idSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  findByclasss_idSuccess: function(item) {
    return { type: types.FINDBYCLASSS_ID_STUDENT_SUCCESS, payload: item };
  },


  // Find by gender_id
  findBygender_id: function(key) {
    return function(dispatch) {
      return StudentApi
        .findBygender_id(key)
        .then(item => {
          dispatch(actionsFunction.findBygender_idSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  findBygender_idSuccess: function(item) {
    return { type: types.FINDBYGENDER_ID_STUDENT_SUCCESS, payload: item };
  },


  // Find by role_id
  findByrole_id: function(key) {
    return function(dispatch) {
      return StudentApi
        .findByrole_id(key)
        .then(item => {
          dispatch(actionsFunction.findByrole_idSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  findByrole_idSuccess: function(item) {
    return { type: types.FINDBYROLE_ID_STUDENT_SUCCESS, payload: item };
  },


  // Find by school_id
  findByschool_id: function(key) {
    return function(dispatch) {
      return StudentApi
        .findByschool_id(key)
        .then(item => {
          dispatch(actionsFunction.findByschool_idSuccess(item));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  findByschool_idSuccess: function(item) {
    return { type: types.FINDBYSCHOOL_ID_STUDENT_SUCCESS, payload: item };
  },


  // Get student
  loadStudent: function(id) {
    return function(dispatch) {
      return StudentApi
        .getOneStudent(id)
        .then(student => {
          dispatch(actionsFunction.loadStudentSuccess(student));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadStudentSuccess: function(student) {
    return { type: types.GET_STUDENT_SUCCESS, payload: student };
  },

  // Load  list
  loadStudentList: function() {
    return function(dispatch) {
      return StudentApi
        .getStudentList()
        .then(list => {
          dispatch(actionsFunction.loadStudentListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadStudentListSuccess: function(list) {
    return { type: types.LIST_STUDENT_SUCCESS, payload: list };
  },

	
  // Save student
  saveStudent: function(student) {
    return function(dispatch) {
      return StudentApi
        .saveStudent(student)
        .then(student => {
          dispatch(actionsFunction.saveStudentSuccess(student));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveStudentSuccess: function(student) {
    return { type: types.UPDATE_STUDENT_SUCCESS, payload: student };
  },


};

export default actionsFunction;
